    handleOnClick = elem => {

        const shuffleArray = arr => arr.sort(() => Math.random() - 0.5);
        const sArray = shuffleArray(this.state.cards);
        const key = elem.target.nodeName;
        this.setState({
            cards: sArray
         });
         console.log(this.state.cards)
         console.log(key.value)
      };

    render() {
        const card = this.state.cards.map((cards,i) => {
            return(

                    <div className ="col s3" key={cards} onClick={this.handleOnClick}>
                    <div className="Card" >
                        <div className="card-image">
                            <img src={"./img/"+cards+".jpeg"} alt="" key={cards}></img>
                        </div>
                    </div>
                    </div>


            )            
        })
        return (
            <div>
                {card}
            </div>


        );
    }
}

export default Cards;
